                    index: parseInt(index),
                    path: `${root}/${v}`,
                    fold,
                    date: mdFile.data?.date || '99999999',
                    ...mdFile.data,
                    content: mdFile.content
                };

                temp[`post_${index}`] = item;
                utils.contents.push(item);
            }
        }


    })

    return temp
}
