        // return () => { console.log('the component is being destoryed') }
    })

    onDestory(() => {
        console.log('the component is being destoryed')
    })

    let clicked = $state(false);

    const handleClick = async () => {
        // 상태 변경
        clicked = true;

        // clicked 값의 상태가 변경 될때까지 대기
        await tick();

